package DoWhileLoop;

public class DoWhileLoopdemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=5;
				
				do
				{ 
					System.out.println(a);
					a++;
				}
		
		while (a<=10); //because the condition is false we get 20. the loop stops
		
		
				
	}

}
